@import './variables.css';
@import './breakpoints.css';

/* 전역 스타일 */
*,
*::before,
*::after {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

html, body, #root {
  width: 100%;
  height: 100vh;
  margin: 0;
  padding: 0;
  overflow: hidden;
}

body {
  font-family: Arial, sans-serif;
}

/* 레이아웃 기본 설정 */
.layout-container {
  width: 100%;
  height: 100vh;
  display: flex;
  flex-direction: column;
  overflow: auto;
}

main {
  flex: 1;
  width: 100%;
  max-width: 1200px;
  margin: 0 auto;
  padding: 16px;
}

/* 공통 컴포넌트 스타일 */
.button {
  padding: 8px 16px;
  border-radius: 4px;
  border: none;
  cursor: pointer;
  transition: all 0.3s ease;
}

.input-field {
  width: 100%;
  padding: 8px;
  border: 1px solid #ddd;
  border-radius: 4px;
}

/* 공통 컨테이너 레이아웃 */
.page-container {
  width: 100%;
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
}

/* 공통 컨텐츠 레이아웃 */
.content-container {
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 20px;
}

/* 공통 입력 필드 그룹 */
.input-group {
  width: 100%;
  max-width: 600px;
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  justify-content: center;
  align-items: center;
}

/* 반응형 미디어 쿼리 */
@media screen and (max-width: 768px) {
  .page-container {
    padding: 16px;
  }

  .input-group {
    flex-direction: column;
    max-width: 100%;
  }

  /* 모바일에서 입력 필드 전체 너비 사용 */
  .input-group input,
  .input-group select,
  .input-group button {
    width: 100%;
    min-width: unset;
  }
}

/* 공통 폼 스타일 */
form {
  width: 100%;
  max-width: 600px;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 16px;
}

/* 공통 버튼 스타일 */
button {
  min-width: 120px;
  padding: 8px 16px;
  border-radius: 4px;
  border: 2px solid #000000;
  background-color: #FFFF00;
  cursor: pointer;
  transition: all 0.2s ease;
}

/* 공통 입력 필드 스타일 */
input, select {
  width: 100%;
  padding: 8px;
  border: 2px solid #000000;
  border-radius: 4px;
  background-color: #ffffff;
  color: #000000;
}
